#![allow(non_snake_case)]
use dioxus::prelude::*;

pub fn Icon(props: IconProps) -> Element {
    let pth = props.icon.get_path();
    rsx! {
        svg {
            class: "h-6 w-6 shrink-0 text-gray-400 group-hover:text-indigo-600",
            fill: "none",
            view_box: "0 0 24 24",
            stroke_width: "1.5",
            stroke: "currentColor",
            path { stroke_linecap: "round", stroke_linejoin: "round", d: pth }
        }
    }
}

